import React, {useEffect, useRef, useState} from "react";
import {auth, db} from "../../config/firebase-config.js"
import {useAuth} from "../../context/AuthContext.js";
import {doc} from "firebase/firestore";

const {currentUser} = useAuth();
const deleteAccount = async (password, userId) => {
  try {
    // Verifica la password dell'utente attuale

    const credentials = await auth.EmailAuthProvider.credential(currentUser.email, password);
   // const patientUserCredential = await auth.createUserWithEmailAndPassword(formData.email_paziente, randomPassword);
      //const uidPatient = credentials.user.uid;
      const uidPatient = currentUser.uid;
    await currentUser.reauthenticateWithCredential(credentials);

    // Elimina l'utente da Firebase Authentication
    await currentUser.delete();

    // Elimina l'utente da Firestore
    await doc(db,'users',uidPatient).delete();

    // Restituisci true se l'eliminazione è avvenuta con successo
    return true;
  } catch (error) {
    console.error('Errore durante l\'eliminazione dell\'account', error);
    throw error;
  }
};

export default deleteAccount;
