  //Funzione per gestire il cambio di stato delle checkbox
  const handleCheckboxChange = (tipo, id, isChecked) => {
    setFormData((prevFormData) => {
        const newArray = [...prevFormData[tipo]];
        const itemIndex = newArray.findIndex(item => item.id === id);
        newArray[itemIndex].selected = isChecked;
        return { ...prevFormData, [tipo]: newArray };
    });
};

