  }

  export async function createPatient(uidCurr, formData) {
      try {
           // Genera una password casuale
      const randomPassword = generateRandomPassword();

      // Crea l'account con la password casuale
      const patientUserCredential = await auth.createUserWithEmailAndPassword(formData.email, randomPassword);

      // Invia un'email al paziente con il link per la modifica della password
      await auth.sendPasswordResetEmail(formData.email);

      // Salva il ruolo dell'utente come "paziente" nel database Firestore
      await setDoc(doc(db, 'users', patientUserCredential.user.uid), {
        email: patientUserCredential.user.email,
        role: 'Utente',
        nutrizionistaId: uidCurr,
        // Altri dati del paziente
      });

      console.log('Paziente registrato con successo. Email inviata per la modifica della password.');
    } catch (error) {
      console.error('Errore durante la registrazione del paziente:', error.message);
