import React, {useEffect, useState} from "react";
import Container from "react-bootstrap/Container";
import LandingPageImage from "../../img/LandingPage-2x.png";
import {Button} from "react-bootstrap";
import {scrollToTop} from "../helpers/scroll";

/*
 Funzione che mostra la LandingPage del sito 'YourFoodCoach'
 Contiene un link al bot telegram 'YourFoodCoachBot'
 */
export default function LandingPage() {
    const [imageLoaded, setImageLoaded] = useState(false);

    //Ad ogni caricamento la pagina effettua uno scroll fino al suo inizio
    useEffect(() => {
        scrollToTop();
    }, []);

    return (
        <>
            <Container className={"modal-fullscreen d-flex flex-column"}
                       style={{
                           background: "linear-gradient(196deg, rgba(238,212,117,255) 45%, rgba(250,153,103,1) 70%, rgba(254,87,94,1) 100%)",
                           backgroundSize: "cover",
                           minHeight: "inherit"
                       }}>
                <Container className={"my-5 my-md-0 my-lg-0 text-center col-12"}>
                    <Container className={"my-5 my-md-4 my-lg-0 text-center col-12"}
                               style={{minHeight: 300}}>
                        <img src={LandingPageImage}
                             alt={"Landing page"}
                             className={"img-fluid"}
                             style={{maxHeight: 700}}
                             onLoad={() => setImageLoaded(true)}
                        />
                        <Container className={"col-12 my-5 my-md-0 my-lg-0 mt-auto"}
                                   style={{padding: 0}}>
                            <Container className={"d-flex justify-content-evenly my-auto"}>
                                {imageLoaded &&
                                    <Button aria-label={"Pulsante Telegram"}
                                            className={"d-flex justify-content-center my-5 my-lg-0"}
                                            style={{borderRadius: 45, width: 190}}
                                            onClick={() => window.open("https://t.me/YourFoodCoachBot")}>
                                        <p className={"mx-auto my-auto"}>Utilizza ChatBot</p>
                                        <svg xmlns="http://www.w3.org/2000/svg"
                                             width="30"
                                             height="30"
                                             fill="currentColor"
                                             className="bi bi-telegram"
                                             viewBox="0 0 16 16">
                                            <path
                                                d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM8.287 5.906c-.778.324-2.334.994-4.666 2.01-.378.15-.577.298-.595.442-.03.243.275.339.69.47l.175.055c.408.133.958.288 1.243.294.26.006.549-.1.868-.32 2.179-1.471 3.304-2.214 3.374-2.23.05-.012.12-.026.166.016.047.041.042.12.037.141-.03.129-1.227 1.241-1.846 1.817-.193.18-.33.307-.358.336a8.154 8.154 0 0 1-.188.186c-.38.366-.664.64.015 1.088.327.216.589.393.85.571.284.194.568.387.936.629.093.06.183.125.27.187.331.236.63.448.997.414.214-.02.435-.22.547-.82.265-1.417.786-4.486.906-5.751a1.426 1.426 0 0 0-.013-.315.337.337 0 0 0-.114-.217.526.526 0 0 0-.31-.093c-.3.005-.763.166-2.984 1.09z"/>
                                        </svg>
                                    </Button>}
                            </Container>
                        </Container>
                    </Container>
                </Container>
            </Container>
        </>
    );
}
