import React, {useEffect, useState} from "react";
import {Alert, Badge, Button, Card, Form, Modal, Nav} from "react-bootstrap";
import {useNavigate} from "react-router-dom";
import Container from "react-bootstrap/Container";
import {faAngleLeft, faAngleRight, faCheck, faFileImport, faPlus, faXmark} from "@fortawesome/free-solid-svg-icons";
import {addPatient } from "../../context/PatientContext"
import PatientSections from "../Patient/PatientSections";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {useAuth} from "../../context/AuthContext";
//import {Validator} from "@marketto/codice-fiscale-utils";
import {fetchUser} from "../../context/UserContext";
import {scrollToTop} from "../helpers/scroll";
import {collection, doc, setDoc, addDoc, getDocs, orderBy, query, where, getFirestore, arrayUnion, updateDoc } from "firebase/firestore";
import {auth, db} from "../../config/firebase-config";
import {LinkContainer} from "react-router-bootstrap";
import Select from "react-select";
import {SyncLoader} from "react-spinners";
import { uriDecode } from "@sideway/address";



export default function NewPatient() {
    const {currentUser} = useAuth();
    const navigate = useNavigate();
    /*const [paziente, setPaziente] = useState({
        nome: "",
        cognome: "",
        email: "",
        telefono: "",
        sesso: "",
        nascita: "",
        luogo_nascita: "",
        provincia_nascita: "",
        citta_res: "",
        prov_res: "",
        via: "",
    }); */
    const [warning, setWarning] = useState("");
    const [error, setError] = useState("");
    const [activeSection, setActiveSection] = useState(0);
    const [section, setSection] = useState(0);
    const [disabled] = useState(false);
    const [loading, setLoading] = useState(false);
    const [progress, setProgress] = useState(false);
    const [checkAnagrafica, setCheckAnagrafica] = useState(false);
    const [checkAnanmesi, setCheckAnamnesi] = useState(false);
    const [checkEsami, setCheckEsami] = useState(false);
    const [checkMisureAntro, setCheckMisureAntro] = useState(false);
    const [checkMisure, setCheckMisure] = useState(false);
    const [CheckAllergie, setCheckAllergie] = useState(false);
    const [CheckMalattie, setCheckMalattie] = useState(false);

    const [exists, setExists] = useState(false);
    const [userInput, setUserInput] = useState([]);
    const [showDetails, setShowDetails] = useState(false);


    const [formData, setFormData] = useState({
    email_nutrizionista: "",
    numero_telefono_nutrizionista: "",
    telegram_bot_passcode: "",
    telegram_user_id: "",
    telegram_chat_id: "",
    telegram_alerts: true,
    telegram_alerts_acqua: true,
    tipologia_dieta: "",
    data_inserimento: "",
    nome_paziente: "",
    cognome_paziente: "",
    email_paziente: "",
    numero_telefono_paziente: "",
    data_nascita_paziente:"",
    luogo_nascita_paziente:"",
    provincia_nasc_paziente:"",
    citta_resid_paziente:"",
    provincia_resid_paziente:"",
    via_residenza_paziente:"",
    sesso_paziente: "",
    codice_fiscale_paziente: "",
    note_anamnesi: "",
    allergie: {
        celiachia:false,
        glutine: false,
        uova: false,
        frutta_secca: false, 
    },
    intolleranze: {
        celiachia:false,
        glutine: false,
        uova: false,
        frutta_secca: false,
    },


    esami_prescritti:"",
    esami_effettuati:"",

    data_inizio_dieta: "",
    data_fine_dieta: "",
    note_anamnesi: "",

    peso_paziente: "",
    altezza_paziente: "",
    circ_fianchi:"",
    circ_braccia:"",
    circ_cosce:"",
    circ_vita:"",  
    circ_spalle:"",
    circ_torace:"",
    circ_testa:"",
    circ_collo:"",

    obiettivo_peso: "",
    obiett_fianchi:"",
    obiett_braccia:"",
    obiett_cosce:"",
    obiett_vita:"",  
    obiett_spalle:"",
    obiett_torace:"",
    obiett_testa:"",
    obiett_collo:"",

    carenze_vitam:"",
    acqua_intrace:"",
    acqua_extrace:"",
    massa_met_att:"",
    met_basale:"",
    massa_magra:"",
    massa_grassa:"",
    angolo_bia:"",

    obiett_acqua_intrace:"",
    obiett_acqua_extrace:"",
    obiett_massa_met_att:"",
    obiett_met_basale:"",
    obiett_massa_magra:"",
    obiett_massa_grassa:"",
    obiett_angolo_bia:"",

    celiachia:"",
    glutine:"",
    uova:"",
    frutta_secca:"",
    frutta_fresca:"",
    soia:"",
    grano:"",
    lattosio:"",
    crostacei:"",
    nichel:"",
    intolleranze:[],
    allergie:[],
    altroAllergie:"",
    altroIntolleranze:"",

    diabete:"",
    obesita:"",
    anoressia:"",
    cardio:"",
    tumore:"",
    ipotiroidismo:"",
    ipertiroidismo:"",
    ipercolesterolemia:"",
    depressione:"",
    ansia:"",
    altroPatologie:"",

}); 
const crypto = require('crypto');

function generateRandomPassword() {
  return crypto.randomBytes(8).toString('hex');
}

let uidCurr = currentUser.uid;
const createPatient = async (uidCurr, formData) => {
    try {
         // Genera una password casuale
    const randomPassword = generateRandomPassword();

    // Crea l'account con la password casuale
    const patientUserCredential = await createUserWithEmailAndPassword(auth, patientData.email, randomPassword);

    // Invia un'email al paziente con il link per la modifica della password
    await sendPasswordResetEmail(auth, formData.email);

    // Salva il ruolo dell'utente come "paziente" nel database Firestore
    await setDoc(doc(db, 'users', patientUserCredential.user.uid), {
      email: patientUserCredential.user.email,
      role: 'paziente',
      nutrizionistaId: uidCurr,
      // Altri dati del paziente
    });

    console.log('Paziente registrato con successo. Email inviata per la modifica della password.');
  } catch (error) {
    console.error('Errore durante la registrazione del paziente:', error.message);
    //throw error;
} 
    };


const handleCreatePatient = async () => {
          try {
            setLoading(true);

       // Esegui il controllo che tutte le sezioni siano complete
    if (!checkAnagrafica || !checkEsami || !checkMisureAntro || !checkMisure) {
        setLoading(false);
        setProgress(false);
        scrollToTop();
        return setError("Per poter procedere con la creazione del paziente devi prima completare tutti i campi obbligatori.");
      }  

  //Controllo che il numero di telefono inserito sia valido
    if (formData.numero_telefono_paziente.length !== 10) {
    setProgress(false);
    setLoading(false);
    scrollToTop();
    return setError("Il numero di telefono inserito non è valido");
}

//Controllo che non vi sia già una dieta esistente con la stessa email paziente
if (exists) {
    setProgress(false);
    setLoading(false);
    scrollToTop();
    return setError("L'email immessa è già associata ad un paziente esistente");
}
if (formData.codice_fiscale_paziente.length !== 16 /*&& !Validator.codiceFiscale(formData.codice_fiscale_paziente).valid*/) {
    setLoading(false);
    setProgress(false);
    scrollToTop();
    return setError("Il codice fiscale inserito non è corretto.");
