import {Alert, Button, Card, FloatingLabel, Form, Modal, Row} from "react-bootstrap";
import Container from "react-bootstrap/Container";
import React, {useEffect, useState} from "react";
import {collection, getDocs, query, where} from "firebase/firestore";
import {db} from "../../config/firebase-config";
import Select from "react-select";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPlus} from "@fortawesome/free-solid-svg-icons";
import axios from "axios";


const PatientSections = ({
            section,
            disabled,
            setProgress,
            setLoading,
            setUserInput,
            setWarning,
            setError,
            reloadOptions,
            setReloadOptions
        }) => {

const cardBackgroundColor = {
background: "#FFEAD3"
};
const [showModal, setShowModal] = useState(false);
const [newExamText, setNewExamText] = useState('');
const [newExam, setNewExam] = useState('');
const [formData, setFormData] = useState({
    nome_paz: "",
    cognome_paz: "",
    email_paz: "",
    tel_paz: "",
    sesso: "",
    data_nascita_paz: "",
    luogo_nascita_paz: "",
    provincia_nascita_paz: "",
    citta_resid_paz: "",
    provincia_resid_paz: "",
    via_resid_paz: "",
    cf_paz: "",
    esami_effettuati: "",
    esami_prescritti: "",
    allergie: [], // Un array per tenere traccia delle allergie selezionate
    intolleranze: [], // Un array per tenere traccia delle intolleranze selezionate
