import { auth, db } from "../../config/firebase-config.js";
import { doc } from "firebase/firestore";

const DeleteAccount = async (password, currentUser) => {
  try {
    if (!currentUser) {
      throw new Error('Utente non autenticato');
    }

    // Verifica la password dell'utente attuale
    const credentials = auth.EmailAuthProvider.credential(currentUser.email, password);
    await currentUser.reauthenticateWithCredential(credentials);

    // Elimina l'utente da Firebase Authentication
    await auth.deleteUser(currentUser.uid);

    // Elimina l'utente da Firestore
    await doc(db, 'users', currentUser.uid).delete();

    // Restituisci true se l'eliminazione è avvenuta con successo
    return true;
  } catch (error) {
    console.error('Errore durante l\'eliminazione dell\'account', error);
    throw error;
  }
};

export default DeleteAccount;
